INSERT INTO `blog_about` VALUES (1, '<h1 style=\"box-sizing: border-box; margin: 20px 0px 10px; font-size: 45px; font-family: \" pt=\"\" font-weight:=\"\" line-height:=\"\" color:=\"\" font-style:=\"\" font-variant-ligatures:=\"\" font-variant-caps:=\"\" letter-spacing:=\"\" orphans:=\"\" text-align:=\"\" text-indent:=\"\" text-transform:=\"\" white-space:=\"\" widows:=\"\" word-spacing:=\"\" -webkit-text-stroke-width:=\"\" background-color:=\"\" text-decoration-style:=\"\" text-decoration-color:=\"\"><span style=\"box-sizing: border-box; vertical-align: inherit;\"><span style=\"box-sizing: border-box; vertical-align: inherit;\">你好，我叫</span></span><span class=\"about-bold\" style=\"box-sizing: border-box; font-weight: bold;\"><span style=\"box-sizing: border-box; vertical-align: inherit;\"><span style=\"box-sizing: border-box; vertical-align: inherit;\"> YangYi<br/></span></span></span></h1><p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: rgb(68, 68, 68); font-family: \" open=\"\" font-size:=\"\" font-style:=\"\" font-variant-ligatures:=\"\" font-variant-caps:=\"\" font-weight:=\"\" letter-spacing:=\"\" orphans:=\"\" text-align:=\"\" text-indent:=\"\" text-transform:=\"\" white-space:=\"\" widows:=\"\" word-spacing:=\"\" -webkit-text-stroke-width:=\"\" background-color:=\"\" text-decoration-style:=\"\" text-decoration-color:=\"\"><span style=\"box-sizing: border-box; vertical-align: inherit;\"><span style=\"box-sizing: border-box; vertical-align: inherit;\">yang存有悲坐阿梅德，consectetur adipiscing ELIT。</span><span style=\"box-sizing: border-box; vertical-align: inherit;\">拒绝一些与持久遭遇大总管的性质的不适拒绝一个跟着一个，没有什么我们认为他们的应对腐败！</span><span style=\"box-sizing: border-box; vertical-align: inherit;\">不知道她是如何通过我们对他的劳动，所接受的东西蒙蔽？</span></span></p><p style=\"box-sizing: border-box; margin: 0px 0px 10px; color: rgb(68, 68, 68); font-family: \" open=\"\" font-size:=\"\" font-style:=\"\" font-variant-ligatures:=\"\" font-variant-caps:=\"\" font-weight:=\"\" letter-spacing:=\"\" orphans:=\"\" text-align:=\"\" text-indent:=\"\" text-transform:=\"\" white-space:=\"\" widows:=\"\" word-spacing:=\"\" -webkit-text-stroke-width:=\"\" background-color:=\"\" text-decoration-style:=\"\" text-decoration-color:=\"\"><span style=\"box-sizing: border-box; vertical-align: inherit;\"><span style=\"box-sizing: border-box; vertical-align: inherit;\">Lorem存有悲坐阿梅德，consectetur adipiscing ELIT。</span><span style=\"box-sizing: border-box; vertical-align: inherit;\">谁是这些男人更苛刻，因为疼痛他，幸福，不过丢在他们接受了最终增强除非是少的事情，他恨，一场伟大的胜利伟大办公室的痛苦。</span></span></p><p><br/></p>');

-- ----------------------------
-- Table structure for blog_article
-- ----------------------------
DROP TABLE IF EXISTS `blog_article`;
CREATE TABLE `blog_article`  (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'id',
  `title` varchar(40) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT 'title',
  `author` char(10) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '作者',
  `thumb` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '缩略图',
  `tag` varchar(10) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '标签',
  `content` text CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '内容',
  `count` int(10) NOT NULL COMMENT '浏览次数',
  `date` int(10) NOT NULL COMMENT 'date',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 20 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Compact;

